import React from "react";
import Card from "react-credit-cards";

import {
  formatCreditCardNumber,
  formatCVC,
  formatExpirationDate,
} from "./utils";

import "react-credit-cards/es/styles-compiled.css";
import { Box, Button, Grid, Image, Input, Text } from "@chakra-ui/react";
import { Link, useNavigate } from "react-router-dom";

export default class App extends React.Component {
  state = {
    number: "",
    name: "",
    expiry: "",
    cvc: "",
    issuer: "",
    focused: "",
    formData: null,
  };

  handleCallback = ({ issuer }, isValid) => {
    if (isValid) {
      this.setState({ issuer });
    }
  };

  handleInputFocus = ({ target }) => {
    this.setState({
      focused: target.name,
    });
  };

  handleInputChange = ({ target }) => {
    if (target.name === "number") {
      target.value = formatCreditCardNumber(target.value);
    } else if (target.name === "expiry") {
      target.value = formatExpirationDate(target.value);
    } else if (target.name === "cvc") {
      target.value = formatCVC(target.value);
    }

    this.setState({ [target.name]: target.value });
  };

  // handlePay=()=>{
  //   window.location.href="/profile"
  // }

  handleSubmit = (e) => {
    e.preventDefault();
    const { issuer } = this.state;
    const formData = [...e.target.elements]
      .filter((d) => d.name)
      .reduce((acc, d) => {
        acc[d.name] = d.value;
        return acc;
      }, {});

    this.setState({ formData });
    this.form.reset();
  };

  render() {
    const { name, number, expiry, cvc, focused, issuer, formData } = this.state;

    return (
      <div key="Payment">
        <div className="App-payment">
          <Text fontSize="2rem" mb="1rem">
            Enter details of your card
          </Text>

          <Card
            number={number}
            name={name}
            expiry={expiry}
            cvc={cvc}
            focused={focused}
            callback={this.handleCallback}
          />

          <form ref={(c) => (this.form = c)} onSubmit={this.handleSubmit}>
            <Box
              // border="1px solid #257CFF"
              mt="2rem"
              width="80%"
              borderRadius="1rem"
              p="1rem"
              boxShadow="rgba(99, 99, 99, 0.2) 0px 2px 8px 0px"
            >
              <div className="form-group">
                <Input
                  mt="0.7rem"
                  type="tel"
                  name="number"
                  className="form-control"
                  placeholder="Enter Card Number"
                  pattern="[\d| ]{16,22}"
                  required
                  onChange={this.handleInputChange}
                  onFocus={this.handleInputFocus}
                />
              </div>
              <div className="form-group">
                <Input
                  mt="0.7rem"
                  type="text"
                  name="name"
                  className="form-control"
                  placeholder="Enter your Name"
                  required
                  onChange={this.handleInputChange}
                  onFocus={this.handleInputFocus}
                />
              </div>
              <div className="row">
                <div className="col-6">
                  <Input
                    mt="0.7rem"
                    type="tel"
                    name="expiry"
                    className="form-control"
                    placeholder="Valid Thru"
                    pattern="\d\d/\d\d"
                    required
                    onChange={this.handleInputChange}
                    onFocus={this.handleInputFocus}
                  />
                </div>
                <div className="col-6">
                  <Input
                    mt="0.7rem"
                    type="tel"
                    name="cvc"
                    className="form-control"
                    placeholder="Enter CVC"
                    pattern="\d{3,4}"
                    required
                    onChange={this.handleInputChange}
                    onFocus={this.handleInputFocus}
                  />
                </div>
              </div>
              <Input mt="0.7rem" type="hidden" name="issuer" value={issuer} />
              <div className="form-actions">
                <button className="btn btn-primary btn-block">
                  <Link to="/profile">
                    <Button
                      // onClick={this.handlePay}
                      bg="#FFA200"
                      borderRadius="1rem"
                      variant="solid"
                      ml="3rem"
                      color="white"
                      _hover={{
                        background: "white",
                        color: "#FFA200",
                        border: "2px solid #FFA200",
                      }}
                      mr={3}
                      //   onClick={handleSubmit}  use onClick for saving new Flight Details
                      mt="1rem"
                      w="10rem"
                    >
                      Pay Now
                    </Button>
                  </Link>
                </button>
              </div>
            </Box>
          </form>

          <hr style={{ margin: "60px 0 30px" }} />

          <Text fontSize="1.5rem" color="gray" mt="2rem">
            Supported Cards
          </Text>

          <Grid display="flex" justifyContent="space-between" mt="2rem">
            <Box display="flex">
              <Text mt="1rem">55...</Text>
              <Image
                w="4rem"
                src="data:image/png;base64,"
              />
            </Box>

            <Box display="flex">
              <Text mt="1rem">41...</Text>
              <Image
                w="4rem"
                src="data:image/png;base64,"
              />
            </Box>

            <Box display="flex">
              <Text mt="1rem">37...</Text>
              <Image
                w="4rem"
                src="data:image/png;base64,"
              />
            </Box>

            <Box display="flex">
              <Text mt="1rem">36...</Text>
              <Image
                w="4rem"
                src="data:image/png;base64,"
              />
            </Box>

            <Box display="flex">
              <Text mt="1rem">6011...</Text>
              <Image
                w="4rem"
                src="data:image/png;base64,"
              />
            </Box>

            <Box display="flex">
              <Text mt="1rem">50...</Text>
              <Image
                w="4rem"
                src="data:image/png;base64,"
              />
            </Box>

            <Box display="flex">
              <Text mt="1rem">62...</Text>
              <Image
                w="4rem"
                src="data:image/png;base64,"
              />
            </Box>

            <Box display="flex">
              <Text mt="1rem">6062...</Text>
              <Image
                w="3rem"
                src="data:image/png;base64,"
              />
            </Box>
          </Grid>

          <hr style={{ margin: "60px 0 30px" }} />
        </div>
      </div>
    );
  }
}
